import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BusStopDisplay from './BusStopDisplay';
import BusStopForm from './BusStopForm';

const API_URL = 'https://cors-anywhere.herokuapp.com/http://data.foli.fi/siri/sm/';

const App = () => {
  const [arrivals, setArrivals] = useState([]);
  const [error, setError] = useState(null);

  const fetchArrivals = async (stopNumber) => {
    try {
      const response = await axios.get(`${API_URL}${stopNumber}`);
      const arrivalsData = response.data;
      // Calculate delay for each arrival
      const now = new Date();
      const updatedArrivals = arrivalsData.map(arrival => {
        const expectedArrivalTime = new Date(arrival.expectedarrivaltime);
        const delay = Math.floor((expectedArrivalTime.getTime() - now.getTime()) / 60000);
        return {
          ...arrival,
          delay,
          early: delay < 0
        };
      });
      setArrivals(updatedArrivals);
      setError(null);
    } catch (error) {
      setArrivals([]);
      setError('Error fetching arrival data');
    }
  };

  useEffect(() => {
    // Fetch initial data when component mounts
    fetchArrivals('164'); // Example stop number
    // Auto-update every 30 seconds
    const interval = setInterval(() => {
      fetchArrivals('164'); // Example stop number
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Bus Stop Display</h1>
      <BusStopForm onSubmit={fetchArrivals} />
      {error && <p>{error}</p>}
      <BusStopDisplay arrivals={arrivals} />
    </div>
  );
};

export default App;
