import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BusStopForm from './BusStopForm';
import BusStopDisplay from './componentsBusStopDisplay';

const API_URL = 'http://data.foli.fi/siri/sm/';

const App = () => {
  const [stopName, setStopName] = useState('');
  const [arrivals, setArrivals] = useState([]);
  const [error, setError] = useState('');

  const fetchArrivals = async (stopNumber) => {
    try {
      const response = await axios.get(`${API_URL}${stopNumber}`);
      const { stopname, result } = response.data;
      setStopName(stopname);
      setArrivals(result);
      setError('');
    } catch (error) {
      setStopName('');
      setArrivals([]);
      setError('Error fetching arrival data');
    }
  };

  return (
    <div>
      <h1>Bus Stop Display</h1>
      <BusStopForm onSubmit={fetchArrivals} />
      {error && <p>{error}</p>}
      {stopName && <BusStopDisplay stopName={stopName} arrivals={arrivals} />}
    </div>
  );
};

export default App;
