import React, { useState, useEffect } from 'react';
import BusStopForm from './components/BusStopForm';
import BusStopInfo from './components/BusStopInfo';
import { getArrivalInfo } from './utils/api';

const App = () => {
  const [stopNumber, setStopNumber] = useState('');
  const [arrivalInfo, setArrivalInfo] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const arrivalData = await getArrivalInfo(stopNumber);
        setArrivalInfo(arrivalData);
      } catch (error) {
        console.error('Failed to fetch arrival data:', error);
      }
    };

    fetchData();

    const interval = setInterval(fetchData, 60000); // Обновление каждую минуту

    return () => clearInterval(interval);
  }, [stopNumber]);

  const handleFormSubmit = (stopNumber) => {
    setStopNumber(stopNumber);
  };

  const handleRefresh = () => {
    setArrivalInfo(null); // Сбрасываем текущую информацию
    setStopNumber(stopNumber); // Вызываем обновление данных остановки
  };

  return (
    <div>
      <h1>Bus Stop Display Application</h1>
      <BusStopForm onSubmit={handleFormSubmit} />
      <button onClick={handleRefresh}>Refresh</button>
      {arrivalInfo && <BusStopInfo stopName={arrivalInfo.name} arrivals={arrivalInfo.arrivals} />}
    </div>
  );
};

export default App;
