// BusStopDisplay.js
import React from 'react';

const BusStopDisplay = ({ stopName, arrivals }) => {
  const now = new Date();

  return (
    <div>
      <h2>{stopName}</h2>
      <ul>
        {arrivals.map((arrival, index) => (
          <li key={index}>
            <strong>Destination:</strong> {arrival.destinationdisplay}<br />
            <strong>Scheduled Arrival:</strong> {new Date(arrival.expectedarrivaltime).toLocaleTimeString()}<br />
            <strong>Status:</strong> {arrival.early ? 'Early by' : 'Late by'} {Math.abs(arrival.delay)} minutes
            <br />
            <strong>Real-time Arrival:</strong> {new Date(now.getTime() + arrival.delay * 60000).toLocaleTimeString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BusStopDisplay;
