import axios from 'axios';

export const getAllStops = async () => {
  try {
    const response = await axios.get('/api/siri/sm');
    return response.data;
  } catch (error) {
    console.error('Failed to fetch stop data:', error);
    throw error;
  }
};

export const getArrivalInfo = async (stopNumber) => {
  try {
    const response = await axios.get(`/api/siri/sm/${stopNumber}`);
    return response.data;
  } catch (error) {
    console.error(`Failed to fetch arrival data for stop ${stopNumber}:`, error);
    throw error;
  }
};
