// src/components/BusStopForm.js
import React, { useState } from 'react';
import axios from 'axios';

const BusStopForm = () => {
  const [stopNumber, setStopNumber] = useState('');
  const [arrivalInfo, setArrivalInfo] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await axios.get(`/api/siri/sm/${stopNumber}`);
      setArrivalInfo(response.data);
    } catch (error) {
      console.error('Failed to get arrival info:', error);
      setError('Failed to get arrival info');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Enter bus stop number" 
          value={stopNumber} 
          onChange={(e) => setStopNumber(e.target.value)} 
        />
        <button type="submit">Get Arrival Info</button>
      </form>
      {error && <p>{error}</p>}
      {arrivalInfo && (
        <div>
          <h2>Arrival Information</h2>
          <p>Destination: {arrivalInfo.destinationdisplay}</p>
          <p>Scheduled Arrival Time: {arrivalInfo.expectedarrivaltime}</p>
          <p>Arrival Status: {arrivalInfo.early ? 'Early' : 'Late'} by {arrivalInfo.delay} minutes</p>
        </div>
      )}
    </div>
  );
};

export default BusStopForm;
