// src/components/BusStopForm.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const BusStopForm = () => {
  const [stopNumber, setStopNumber] = useState("");
  const [arrivalInfo, setArrivalInfo] = useState(null);
  const [buses, setBuses] = useState([]);
  const [selectedBus, setSelectedBus] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBuses = async () => {
      try {
        const response = await axios.get("http://data.foli.fi/siri/sm");
        setBuses(response.data);
      } catch (error) {
        console.error("Failed to fetch bus list:", error);
        setError("Failed to fetch bus list");
      }
    };

    fetchBuses();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const response = await axios.get(`/api/siri/sm/${stopNumber}`);
      setArrivalInfo(response.data);
    } catch (error) {
      console.error("Failed to get arrival info:", error);
      setError("Failed to get arrival info");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="busStopNumber">Enter bus stop number:</label>
        <input
          type="text"
          id="busStopNumber"
          value={stopNumber}
          onChange={(e) => setStopNumber(e.target.value)}
        />
        <label htmlFor="busSelect">Select a bus:</label>
        <select
          id="busSelect"
          value={selectedBus}
          onChange={(e) => setSelectedBus(e.target.value)}
        >
          <option value="">Select a bus</option>
          {buses.map((bus) => (
            <option key={bus.number} value={bus.number}>
              {bus.name}
            </option>
          ))}
        </select>
        <button type="submit">Get Arrival Info</button>
      </form>
      {error && <p>{error}</p>}
      {arrivalInfo && (
        <div>
          <h2>Arrival Information</h2>
          <p>Destination: {arrivalInfo.destinationdisplay}</p>
          <p>Scheduled Arrival Time: {arrivalInfo.expectedarrivaltime}</p>
          <p>
            Arrival Status: {arrivalInfo.early ? "Early" : "Late"} by{" "}
            {arrivalInfo.delay} minutes
          </p>
        </div>
      )}
    </div>
  );
};

export default BusStopForm;
