import React from 'react';

const BusStopInfo = ({ stopName, arrivals }) => {
    return (
        <div className="stop-info">
          <h2>Bus Stop: {stopName}</h2>
          <ul>
            {arrivals.map((arrival, index) => (
              <li key={index}>
                <p>Destination: {arrival.destinationdisplay}</p>
                <p>Scheduled Arrival Time: {arrival.expectedarrivaltime}</p>
                <p>Status: {arrival.early ? `Early by ${arrival.early} minutes` : `Late by ${arrival.late} minutes`}</p>
              </li>
            ))}
          </ul>
        </div>
      );
};

export default BusStopInfo;
