// BusStopForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BusStopForm = ({ onSubmit }) => {
  const [stopNumber, setStopNumber] = useState('');
  const [stops, setStops] = useState([]);

  useEffect(() => {
    const fetchStops = async () => {
      try {
        const response = await axios.get('http://data.foli.fi/siri/sm');
        setStops(response.data.stops);
      } catch (error) {
        console.error('Error fetching stops:', error);
      }
    };

    fetchStops();
  }, []);

  const handleChange = (e) => {
    setStopNumber(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(stopNumber);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Select Bus Stop:
        <select value={stopNumber} onChange={handleChange}>
          <option value="">Select a bus stop</option>
          {stops.map((stop, index) => (
            <option key={index} value={stop.stopnumber}>{stop.stopname}</option>
          ))}
        </select>
      </label>
      <button type="submit">Get Arrival Info</button>
    </form>
  );
};

export default BusStopForm;
