// BusStopForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BusStopForm = ({ onSubmit }) => {
  const [stops, setStops] = useState([]);
  const [selectedStop, setSelectedStop] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStops = async () => {
      try {
        const response = await axios.get('http://data.foli.fi/siri/sm');
        setStops(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stops:', error);
      }
    };

    fetchStops();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(selectedStop);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Select Destination:
        <select value={selectedStop} onChange={(e) => setSelectedStop(e.target.value)}>
          <option value="">Select a destination</option>
          {loading ? (
            <option disabled>Loading stops...</option>
          ) : (
            stops.map((stop, index) => (
              <option key={index} value={stop.stopcode}>{stop.stopname}</option>
            ))
          )}
        </select>
      </label>
      <button type="submit">Get Arrival Info</button>
    </form>
  );
};

export default BusStopForm;
