import React from 'react';

const BusStopDisplay = ({ stopName, arrivals }) => {
  return (
    <div>
      <h2>Bus Stop Information</h2>
      {stopName && <p>Stop Name: {stopName}</p>}
      <ul>
        {arrivals.map((arrival, index) => (
          <li key={index}>
            <strong>Destination:</strong> {arrival.destinationdisplay}<br />
            <strong>Scheduled Arrival:</strong> {arrival.expectedarrivaltime}<br />
            <strong>Delay:</strong> {arrival.delay} minutes
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BusStopDisplay;
