import React, { useState } from 'react';
import axios from 'axios';

const BusStopForm = ({ onSubmit }) => {
  const [stopNumber, setStopNumber] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://data.foli.fi/siri/sm/${stopNumber}`);
      onSubmit(response.data);
    } catch (error) {
      setError('Failed to fetch arrival data');
      console.error('Error fetching arrival data:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Enter Bus Stop Number:
        <input
          type="text"
          value={stopNumber}
          onChange={(e) => setStopNumber(e.target.value)}
        />
      </label>
      <button type="submit">Get Arrival Info</button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default BusStopForm;
