import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BusStopForm = ({ onSubmit }) => {
  const [buses, setBuses] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBuses = async () => {
      try {
        const response = await axios.get('http://data.foli.fi/siri/sm');
        setBuses(response.data);
      } catch (error) {
        console.error('Failed to fetch bus list:', error);
        setError('Failed to fetch bus list');
      }
    };

    fetchBuses();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const stopNumber = formData.get('stopNumber');
    try {
      await onSubmit(stopNumber);
    } catch (error) {
      console.error('Failed to get arrival info:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Select a bus:
        <select name="stopNumber">
          {buses.map((bus) => (
            <option key={bus.stopNumber} value={bus.stopNumber}>
              {bus.stopName}
            </option>
          ))}
        </select>
      </label>
      <button type="submit">Get Arrival Info</button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default BusStopForm;
