// api.js
import axios from 'axios';

export const getArrivalInfo = async (stopNumber) => {
  try {
    const response = await axios.get(`http://data.foli.fi/siri/sm/${stopNumber}`);
    return response.data;
  } catch (error) {
    console.error('Failed to fetch arrival data for stop:', error);
    throw error;
  }
};
