import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BusStopForm from './components/BusStopForm';
import BusStopInfo from './components/BusStopInfo';

const App = () => {
  const [stopData, setStopData] = useState(null);
  const [stopName, setStopName] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch stop data if stop number is provided
    if (stopName) {
      axios.get(`http://data.foli.fi/siri/sm/${stopName}`)
        .then(response => {
          setStopData(response.data);
          setError('');
        })
        .catch(error => {
          setError('Failed to fetch data. Please enter a valid bus stop number.');
        });
    }
  }, [stopName]);

  const handleSubmit = (stopNumber) => {
    setStopName(stopNumber);
  };

  return (
    <div>
      <h1>Bus Stop Display Application</h1>
      <BusStopForm onSubmit={handleSubmit} />
      {error && <p>{error}</p>}
      {stopData && <BusStopInfo stopName={stopData.name} arrivals={stopData.arrivals} />}
    </div>
  );
};

export default App;
