import React, { useState } from 'react';
import { getArrivalInfo } from '../utils/api';

const BusStopForm = ({ onSubmit }) => {
  const [stopNumber, setStopNumber] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const arrivalInfo = await getArrivalInfo(stopNumber);
      onSubmit(arrivalInfo);
    } catch (error) {
      console.error('Failed to get arrival info:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Enter bus stop number" 
          value={stopNumber} 
          onChange={(e) => setStopNumber(e.target.value)} 
        />
        <button type="submit">Get Bus Stop Info</button>
      </form>
    </div>
  );
};

export default BusStopForm;
