// BusStopDisplay.js
import React from 'react';

const BusStopDisplay = ({ stopName, arrivals, currentTime }) => {
  if (!stopName) {
    return <p>No stop selected</p>;
  }

  return (
    <div>
      <h2>{stopName}</h2>
      <p>Current Time: {currentTime || 'Loading...'}</p>
      {arrivals.length === 0 ? (
        <p>No arrivals</p>
      ) : (
        <ul>
          {arrivals.map((arrival, index) => (
            <li key={index}>
              <strong>Destination:</strong> {arrival.destinationdisplay}<br />
              <strong>Scheduled Arrival:</strong> {new Date(arrival.expectedarrivaltime).toLocaleTimeString()}<br />
              <strong>Status:</strong> {arrival.delay < 0 ? 'Early by' : 'Late by'} {Math.abs(arrival.delay)} minutes
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default BusStopDisplay;
