// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BusStopForm from './components/BusStopForm';
import BusStopDisplay from './components/BusStopDisplay';

const API_URL = 'http://data.foli.fi/siri/v0.1/stop-monitoring';

const App = () => {
  const [stop, setStop] = useState({});
  const [arrivals, setArrivals] = useState([]);
  const [error, setError] = useState(null);

  const fetchArrivals = async (stop) => {
    try {
      const response = await axios.get(`${API_URL}/${stop.stopNumber}`);
      if (response.data.stopName) {
        setStop({ stopName: response.data.stopName, stopNumber: stop.stopNumber });
        setArrivals(response.data.arrivals);
        setError(null);
      } else {
        setError('Stop not found');
      }
    } catch (error) {
      setError('Error fetching arrival data');
    }
  };

  const handleSubmit = (selectedStop) => {
    const stopObj = isNaN(selectedStop) ? { stopName: selectedStop } : { stopNumber: selectedStop };
    fetchArrivals(stopObj);
  };

  return (
    <div>
      <h1>Bus Stop Display</h1>
      <BusStopForm onSubmit={handleSubmit} />
      {error && <p>{error}</p>}
      <BusStopDisplay stop={stop} arrivals={arrivals} />
    </div>
  );
};

export default App;
