import React, { useState } from 'react';
import styles from './BusStopForm.module.css';

const BusStopForm = ({ onSubmit, stops }) => {
  const [selectedStop, setSelectedStop] = useState('');
  const [stopNumber, setStopNumber] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(selectedStop || stopNumber);
  };

  const handleInputChange = (e) => {
    setStopNumber(e.target.value);
  };

  const handleSelectChange = (e) => {
    setSelectedStop(e.target.value);
  };

  return (
    <div className={styles.formContainer}>
      <form onSubmit={handleSubmit} className={styles.form}>
        <div className={styles.formGroup}>
          <label htmlFor="stopSelect" className={styles.label}>Select a Bus Stop:</label>
          <select id="stopSelect" className={styles.input} value={selectedStop} onChange={handleSelectChange}>
            <option value="">Select a stop</option>
            {Array.isArray(stops) && stops.map((stop) => (
              <option key={stop.stopcode} value={stop.stopcode}>{stop.stopname}</option>
            ))}
          </select>
        </div>
        <div className={styles.formGroup}>
          <label htmlFor="stopNumber" className={styles.label}>Or Enter Stop Number:</label>
          <input
            type="text"
            id="stopNumber"
            className={styles.input}
            value={stopNumber}
            onChange={handleInputChange}
            placeholder="Enter stop number"
          />
        </div>
        <button type="submit" className={styles.button}>Get Arrival Info</button>
      </form>
    </div>
  );
};

export default BusStopForm;
