// BusStopForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BusStopForm = ({ onSubmit }) => {
  const [stops, setStops] = useState([]);
  const [selectedStop, setSelectedStop] = useState('');
  const [inputType, setInputType] = useState('number');

  useEffect(() => {
    const fetchStops = async () => {
      try {
        const response = await axios.get('http://data.foli.fi/siri/sm');
        setStops(response.data);
      } catch (error) {
        console.error('Error fetching stops:', error);
      }
    };

    fetchStops();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(selectedStop);
  };

  const handleInputChange = (e) => {
    setSelectedStop(e.target.value);
  };

  const handleSelectChange = (e) => {
    setInputType(e.target.value);
  };

  const renderInputField = () => {
    if (inputType === 'number') {
      return (
        <input
          type="number"
          value={selectedStop}
          onChange={handleInputChange}
          placeholder="Enter stop number"
          required
        />
      );
    } else {
      return (
        <select value={selectedStop} onChange={handleInputChange} required>
          <option value="">Select a stop</option>
          {stops.length > 0 && stops.map((stop, index) => (
            <option key={index} value={stop.stopcode}>{stop.stopname}</option>
          ))}
        </select>
      );
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Search by:
        <select value={inputType} onChange={handleSelectChange}>
          <option value="number">Stop Number</option>
          <option value="name">Stop Name</option>
        </select>
      </label>
      <div>
        <label>
          {inputType === 'number' ? 'Stop Number:' : 'Stop Name:'}
          {renderInputField()}
        </label>
      </div>
      <button type="submit">Get Arrival Info</button>
    </form>
  );
};

export default BusStopForm;
